
$(document).ready(function(){$('#server_type').on('change',function(){$('#mail, #sendmail, #smtp').css('display','none');switch($(this).val().toLowerCase()){case'mail':$('#mail').css('display','block');break;case'sendmail':$('#sendmail').css('display','block');break;case'smtp':$('#smtp').css('display','block');break;}});$('#server_type').trigger('change');});